# Extracting data for plotting
Segment = [entry["Segment"] for entry in data]
features = [entry['Desired Feature'] for entry in data]
portability = [entry['Desired Portability'] for entry in data]
period = [entry["Period"] for entry in data]
Market = [entry['Segment % of Total Industry'] for entry in data]

# Map team names to numeric values
Segment_color_mapping = {"Pragmatists": 0, "Hermits": 1, "Tech Geeks": 2, "Wanderer": 3, "Blue-Bloods": 4}
numeric_segment = [Segment_color_mapping[segment] for segment in Segment]

# Set up 3D plot
fig = plt.figure()
ax = fig.add_subplot(111, projection='3d')

# Plot individual points
scatter = ax.scatter(features, portability, period, c=numeric_segment, s=[share * 5000 for share in Market], alpha=0.7)


# Legend
legend_labels = [segment for segment in set(Segment)]
legend_handles = scatter.legend_elements()[0]
legend = ax.legend(legend_handles, legend_labels, title="Segments")
ax.add_artist(legend)

# Set labels
ax.set_xlabel('Features')
ax.set_ylabel('Portability')
ax.set_zlabel('Period')
ax.set_title('3D Scatter Plot with Market Share Scaling')

# Show plot
plt.show()